sourcesContent:
.article-container {
  background-color: #F1F1F1;
  min-height: 20em;
  padding-top: 68px;
  color: #262626; }

.article-inner p {
  color: #262626; }

.article-inner .title-row {
  margin-bottom: 1.33333rem; }
  .article-inner .title-row h1 {
    font-weight: 700; }
  .article-inner .title-row .topic-name {
    color: #c71b0a;
    font-size: 16px;
    margin-right: 6px; }
    .article-inner .title-row .topic-name > img {
      margin-top: -3px;
      margin-left: 3px; }
  .article-inner .title-row .subtitle {
    color: #808080;
    font-size: 16px; }

.article-inner .article-meta > div {
  margin-bottom: 1.33333rem; }

.article-inner .leading-img {
  margin-bottom: 40px; }

.article-inner .introduction {
  margin-bottom: 80px; }

.article-inner em {
  font-style: italic;
  padding-right: 0.2rem; }

.article-inner h2 {
  font-size: 24px;
  font-weight: 700;
  line-height: 1.5;
  padding-top: 2.5rem; }

.article-inner a {
  border-bottom: 2px #c71b0a solid;
  cursor: pointer;
  transition: 0.5s all ease;
  position: relative; }
  .article-inner a:hover {
    color: #c71b0a; }
  .article-inner a:after {
    position: absolute;
    content: "";
    left: 0;
    bottom: -0.2em;
    height: 0.11rem;
    width: 0;
    background: #c71b0a;
    transition: 0.5s all ease; }
  .article-inner a:hover:after {
    width: 100%; }

.article-inner u {
  text-decoration: none;
  background-color: rgba(199, 26, 10, 0.25); }

@media only screen and (min-width: 768px) and (max-width: 991px) {
  .article-container .title-row {
    width: 556px; }
  .article-container .article-meta {
    width: 556px;
    margin-bottom: 24px; }
  .article-container .introduction {
    width: 556px; } }

@media only screen and (min-width: 992px) {
  .article-container .title-row {
    width: 664px; }
  .article-container .article-meta {
    width: 664px;
    margin-bottom: 40px; }
  .article-container .introduction {
    width: 664px; } }

